<div id="post-<?php the_ID();?>"<?php post_class();?>>  <div class="post-content">    <?php get_template_part('template-parts/content-post-header') ?>    <div>      <?php          if (has_post_thumbnail()) {              the_post_thumbnail();          }          the_content();                    wp_link_pages(array(              'before'      => '<div class="page-links"><span class="page-links-title">' . __('Pages:', 'business-express') . '</span>',              'after'       => '</div>',              'link_before' => '<span>',              'link_after'  => '</span>',              'pagelink'    => '<span class="screen-reader-text">' . __('Page', 'business-express') . ' </span>%',              'separator'   => '<span class="screen-reader-text">, </span>',          ));      ?>    </div>  </div>  <?php echo get_the_tag_list('<p>' . __('Tags:', 'business-express') . '&nbsp;', ' ', '</p>'); ?>  <?php      the_post_navigation(array(          'next_text' => '<span class="meta-nav" aria-hidden="true">' . __('Next:', 'business-express') . '</span> ' .          '<span class="screen-reader-text">' . __('Next post:', 'business-express') . '</span> ' .          '<span class="post-title">%title</span>',          'prev_text' => '<span class="meta-nav" aria-hidden="true">' . __('Previous:', 'business-express') . '</span> ' .          '<span class="screen-reader-text">' . __('Previous post:', 'business-express') . '</span> ' .          '<span class="post-title">%title</span>',      ));  ?>  <div class="post-comments">    <?php       if (comments_open() || get_comments_number()):        comments_template();      endif;    ?>  </div></div>